                    for sub_idx in range(1, kilt_subdiv+1):
                        ssub_idx = '%02d' % sub_idx  
                        c_mas_name = 'c_'+cur_name+'_main_'+sm_idx+'_'+ssub_idx+dup_id+'.x'
                        
