    for i in range(1, max_range):
        id = '%02d' % i
        prev_id = '%02d' % (i - 1)
        next_id = '%02d' % (i + 1)
        c_bone_name = "c_" + spline_name + "_" + id + side_arg
        c_bone_prev_name = "c_" + spline_name + "_" + prev_id + side_arg
        c_bone_next_name = "c_" + spline_name + "_" + next_id + side_arg
        
